 268	 private async callHandler(
 269	 instance: any,
 270	 handler: RestateHandlerMetadata,
 271	 data: Uint8Array,
 272	 ): Promise<Uint8Array> {
 273	 try {
 274	 const args = handler.deserializeArgs(data);
 275	 const result = await instance[handler.name].bind(instance)(...args);
 276	 return serializeRestateHandlerResponse({
 277	 success: true,
 278	 data:
 279	 handler.returnType.kind !== ReflectionKind.void &&
 280	 handler.returnType.kind !== ReflectionKind.undefined
 281	 ? handler.serializeReturn(result)
 282	 : new Uint8Array(),
 283	 // TODO: use entity name
 284	 typeName: handler.returnType.typeName,
 285	 });
