 188	 // TODO: wrap in custom error
 189	 private async executeMiddlewares(
 190	 ctx: RestateBaseContext,
 191	 classMetadata: RestateClassMetadata,
 192	 handlerMetadata?: RestateHandlerMetadata,
 193	 ) {
 194	 for (const middleware of this.module.defaultMiddlewares) {
 195	 await this.injectorContext.get(middleware).execute(ctx);
 196	 }
 197	 for (const middleware of classMetadata.middlewares) {
 198	 await this.injectorContext.get(middleware).execute(ctx);
 199	 }
 200	 if (handlerMetadata) {
 201	 for (const middleware of handlerMetadata.middlewares) {
 202	 await this.injectorContext.get(middleware).execute(ctx);
 203	 }
 204	 }
 205	 }
