 308	 private async callHandler(
 309	 instance: any,
 310	 handler: RestateHandlerMetadata,
 311	 data: Uint8Array,
 312	 ): Promise<Uint8Array> {
 313	 try {
 314	 const args = handler.deserializeArgs(data);
 315	 const result = await instance[handler.name].bind(instance)(...args);
 316	 return serializeRestateHandlerResponse({
 317	 success: true,
 318	 data:
 319	 handler.returnType.kind !== ReflectionKind.void &&
 320	 handler.returnType.kind !== ReflectionKind.undefined
 321	 ? handler.serializeReturn(result)
 322	 : new Uint8Array(),
 323	 // TODO: use entity name
 324	 typeName: handler.returnType.typeName,
 325	 });
