 87	export class RestateServiceDecorator {
 88	 t = new RestateServiceMetadata();
 89	
 90	 onDecorator(classType: ClassType) {
 91	 Object.assign(this.t, { classType });
 92	 }
 93	
 94	 addHandler(action: RestateHandlerMetadata) {
 95	 this.t.handlers.add(action);
