 187	export class RestateHandlerMetadata<T = readonly unknown[]> {
 188	 readonly name: string;
 189	 readonly classType: ClassType;
 190	 readonly returnType: Type;
 191	 readonly argsType: TypeTuple;
 192	 readonly serializeReturn: BSONSerializer;
 193	 readonly deserializeArgs: BSONDeserializer<T>;
 194	 readonly shared?: boolean;
 195	 readonly exclusive?: boolean;
 196	 readonly kafka?: RestateKafkaHandlerMetadata;
 197	 readonly event?: RestateEventHandlerMetadata;
 198	 readonly options?: RestateHandlerOptions;
 199	 readonly middlewares: ClassType<RestateMiddleware>[] = [];
 200	}
 201	
 202	export class RestateHandlerDecorator {
 203	 t = new RestateHandlerMetadata();
 204	
 205	 onDecorator(classType: ClassType, property: string | undefined) {
 206	 if (!property) return;
 207	
 208	 const reflectionClass = ReflectionClass.from(classType);
 209	 const reflectionMethod = reflectionClass.getMethod(property);
 210	
 211	 const returnType =
 212	 getUnwrappedReflectionFunctionReturnType(reflectionMethod);
 213	 const serializeReturn = getResponseDataSerializer(returnType);
 214	
 215	 const argsType = getReflectionFunctionArgsType(reflectionMethod);
 216	 const deserializeArgs =
 217	 this.t.deserializeArgs || getBSONDeserializer(undefined, argsType);
 218	
 219	 Object.assign(this.t, {
 220	 name: property,
 221	 classType,
 222	 returnType,
 223	 serializeReturn,
 224	 argsType,
 225	 deserializeArgs,
 226	 });
...
