 191	 ) {
 192	 for (const middleware of this.module.defaultMiddlewares) {
 193	 await injectorContext.get(middleware).execute(ctx);
 194	 }
 195	 for (const middleware of classMetadata.middlewares) {
 196	 await injectorContext.get(middleware).execute(ctx);
 197	 }
 198	 if (handlerMetadata) {
 199	 for (const middleware of handlerMetadata.middlewares) {
 200	 await injectorContext.get(middleware).execute(ctx);
 201	 }
 202	 }
 203	 }
 204	
 205	 private createServiceHandlers({
 206	 classType,
 207	 module,
 208	 metadata,
 209	 }: InjectorService<unknown>) {
 210	 return [...metadata.handlers].reduce(
 211	 (handlers, handler) => ({
 212	 ...handlers,
 213	 [handler.name]: restate.handlers.handler(
 214	 { ...DEFAULT_HANDLER_OPTS, ...handler.options },
 215	 async (
 216	 rsCtx: restate.Context,
 217	 data: Uint8Array,
 218	 ): Promise<Uint8Array> => {
 219	 const injector = this.createScopedInjector();
 220	 injector.set(InjectorContext, injector);
 221	 const ctx = createServiceContext(rsCtx, this.config);
 222	 injector.set(restateClientType, ctx);
 223	 injector.set(restateSharedContextType, ctx);
 224	 injector.set(restateServiceContextType, ctx);
 225	 await this.executeMiddlewares(injector, ctx, metadata, handler);
 226	 const instance = injector.get(classType, module);
