 305	
 306	 private async callHandler(
 307	 instance: any,
 308	 handler: RestateHandlerMetadata,
 309	 data: Uint8Array,
 310	 ): Promise<Uint8Array> {
 311	 try {
 312	 const args = handler.deserializeArgs(data);
 313	 const result = await instance[handler.name].bind(instance)(...args);
 314	 return serializeRestateHandlerResponse({
 315	 success: true,
 316	 data:
 317	 handler.returnType.kind !== ReflectionKind.void &&
 318	 handler.returnType.kind !== ReflectionKind.undefined
 319	 ? handler.serializeReturn(result)
 320	 : new Uint8Array(),
 321	 // TODO: use entity name
 322	 typeName: handler.returnType.typeName,
 323	 });
