 127	 private provideMiddleware(metadata: RestateClassMetadata): void {
 128	 for (const middleware of metadata.middlewares) {
 129	 if (!this.isProvided(middleware))
 130	 this.addProvider({ provide: middleware, scope: SCOPE });
 131	 }
 132	 for (const handler of metadata.handlers) {
 133	 for (const middleware of handler.middlewares) {
 134	 if (!this.isProvided(middleware))
 135	 this.addProvider({ provide: middleware, scope: SCOPE });
 136	 }
 137	 }
