 59	export function getRestateKafkaTopicSource(type: Type): string {
 60	 const typeArgument = getTypeArgument(type, 0);
 61	 assertType(typeArgument, ReflectionKind.literal);
 62	 if (!(typeof typeArgument.literal === 'string')) {
 63	 throw new Error('Value must be a string');
 64	 }
 65	 return typeArgument.literal;
 66	}
