 59	export class RestateClassMetadata {
 60	 readonly name: string;
 61	 readonly classType: ClassType;
 62	 readonly type: TypeObjectLiteral | TypeClass;
 63	 readonly handlers = new Set<RestateHandlerMetadata>();
 64	 readonly middlewares: ClassType<RestateMiddleware>[] = [];
 65	}
...
 86	
 87	export class RestateServiceDecorator {
 88	 t = new RestateServiceMetadata();
 89	
 90	 onDecorator(classType: ClassType) {
 91	 Object.assign(this.t, { classType });
 92	 }
 93	
 94	 addHandler(action: RestateHandlerMetadata) {
 95	 this.t.handlers.add(action);
 96	 }
 97	
 98	 service<T extends RestateService<string, any>>(
 99	 options?: ServiceOptions,
 100	 type?: ReceiveType<T>,
 101	 ) {
 102	 type = resolveReceiveType(type);
 103	 const name = getRestateClassName(type);
 104	 Object.assign(this.t, {
 105	 options,
 106	 name,
 107	 type,
 108	 });
 109	 }
 110	
 111	 middleware(...middlewares: ClassType<RestateMiddleware>[]) {
 112	 this.t.middlewares.push(...middlewares);
 113	 }
 114	}
 115	
 116	export class RestateObjectDecorator {
 117	 t = new RestateObjectMetadata();
