 21	export function getRestateClassName(serviceType: Type): string {
 22	 const typeArgument = getTypeArgument(serviceType, 0);
 23	 assertType(typeArgument, ReflectionKind.literal);
 24	 return typeArgument.literal as string;
 25	}
 26	
 27	export function getSagaDataType(sagaType: Type): TypeObjectLiteral | TypeClass {
 28	 const typeArgument = getTypeArgument(sagaType, 1);
 29	 if (
 30	 typeArgument?.kind !== ReflectionKind.objectLiteral &&
 31	 typeArgument?.kind !== ReflectionKind.class
 32	 ) {
 33	 throw new Error('Invalid saga data type');
 34	 }
 35	 return typeArgument;
 36	}
