 52	export function getRestateSagaMetadata<T>(
 53	 classType: ClassType,
 54	): RestateSagaMetadata<T> | undefined {
 55	 const metadata = restateSagaDecorator._fetch(classType);
 56	 return metadata?.name ? (metadata as RestateSagaMetadata<T>) : undefined;
 57	}
