 23	
 24	export function createServiceContext(
 25	 ctx: restate.Context,
 26	 config?: RestateConfig,
 27	): RestateServiceContext {
 28	 function propagateRequestHeaders(): Record<string, string> {
 29	 const entries = Object.entries(ctx.request().headers);
 30	 if (config?.server?.propagateIncomingHeaders) {
 31	 return Object.fromEntries(
 32	 entries.filter(([key]) =>
 33	 config?.server?.propagateIncomingHeaders!.includes(key),
 34	 ),
 35	 );
 36	 }
 37	 return Object.fromEntries(entries);
 38	 }
...
 93	 send(...args: readonly any[]): Promise<RestateInvocationHandle> {
 94	 const [key, { service, method, data }, options] =
 95	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 96	
 97	 const headers = config?.server?.propagateIncomingHeaders
 98	 ? {
 99	 ...propagateRequestHeaders(),
 100	 ...options?.headers,
 101	 }
 102	 : options?.headers;
 103	
 104	 const { invocationId } = ctx.genericSend({
 105	 service,
 106	 method,
 107	 parameter: data,
 108	 delay: options?.delay,
 109	 headers,
 110	 key,
 111	 });
 112	
 113	 return invocationId.then(invocationId => ({
 114	 invocationId,
 115	 }));
 116	 },
