 11	
 12	export class RestateServerConfig {
 13	 readonly host?: string;
 14	 readonly port?: number = 9080;
 15	 /**
 16	 * Controls whether incoming request headers are propagated to outgoing service calls.
 17	 * This is useful for passing authentication tokens, correlation IDs, or other
 18	 * context information through the service call chain.
 19	 */
 20	 readonly propagateIncomingHeaders?: string[];
