 1	import { eventDispatcher } from '@deepkit/event';
 2	import {
 3	 onServerMainBootstrap,
 4	 onServerMainShutdown,
 5	} from '@deepkit/framework';
 6	import { InjectorContext } from '@deepkit/injector';
 7	import * as restate from '@restatedev/restate-sdk';
 8	import { LogMetadata } from '@restatedev/restate-sdk';
 9	import { entity, ReflectionKind } from '@deepkit/type';
 10	import { createServer } from 'node:http2';
 11	import { serializeBSON } from '@deepkit/bson';
 12	import { ScopedLogger } from '@deepkit/logger';
...
 49	
 50	export class RestateServer {
 51	 private http2Server?: ReturnType<typeof createServer>;
 52	
 53	 constructor(
 54	 private readonly module: RestateModule,
 55	 private readonly injectorContext: InjectorContext,
 56	 private readonly logger: ScopedLogger,
 57	 ) {}
 58	
 59	 @eventDispatcher.listen(onServerMainShutdown)
 60	 async shutdown() {
 61	 await new Promise(resolve => {
 62	 this.http2Server?.close(resolve);
 63	 });
 64	 }
 65	
 66	 @eventDispatcher.listen(onServerMainBootstrap)
 67	 async bootstrap() {
 68	 const services: restate.EndpointOptions['services'] = [];
 69	
 70	 for (const object of this.module.objects) {
 71	 const handlers = this.createObjectHandlers(object);
 72	 services.push(
 73	 restate.object({
 74	 name: object.metadata.name,
 75	 handlers,
 76	 options: object.metadata.options,
 77	 }),
 78	 );
 79	 }
 80	
 81	 for (const service of this.module.services) {
 82	 const handlers = this.createServiceHandlers(service);
 83	 services.push(
 84	 restate.service({
 85	 name: service.metadata.name,
 86	 handlers,
 87	 options: service.metadata.options,
 88	 }),
 89	 );
 90	 }
 91	
 92	 for (const saga of this.module.sagas) {
 93	 const handlers = this.createSagaHandlers(saga);
 94	 services.push(
 95	 restate.workflow({
 96	 name: saga.metadata.name,
 97	 handlers,
 98	 options: saga.metadata.options,
 99	 }),
 100	 );
 101	 }
 102	
 103	 const handler = restate.createEndpointHandler({
 104	 services,
 105	 defaultServiceOptions: {},
 106	 logger: (
 107	 params: LogMetadata,
 108	 message?: any,
 109	 ...optionalParams: any[]
 110	 ) => {
 111	 if (params.replaying) return;
 112	 if (params.context) {
 113	 this.logger.data(params.context);
 114	 }
 115	 if (params.level === 'trace') return;
 116	 this.logger[params.level](message, ...optionalParams);
 117	 },
 118	 });
 119	
 120	 await new Promise<void>(resolve => {
 121	 this.http2Server = createServer(handler);
 122	 this.http2Server.listen(this.module.config.server?.port!, resolve);
 123	 });
