 50	 request: ctx.request.bind(ctx),
 51	 sleep: ctx.sleep.bind(ctx),
 52	 rejectAwakeable: ctx.rejectAwakeable.bind(ctx),
 53	 genericCall: ctx.genericCall.bind(ctx),
 54	 genericSend: ctx.genericSend.bind(ctx),
 55	 cancel: ctx.cancel.bind(ctx),
 56	 attach<T>(
 57	 invocationId: InvocationId,
 58	 type?: ReceiveType<T>,
 59	 ): RestatePromise<T> {
 60	 const serde = createBSONSerde(type);
 61	 return ctx.attach(invocationId, serde);
 62	 },
...
 93	 send(...args: readonly any[]): Promise<RestateInvocationHandle> {
 94	 const [key, { service, method, data }, options] =
 95	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 96	
 97	 const headers = config?.server?.propagateIncomingHeaders
 98	 ? {
 99	 ...propagateRequestHeaders(),
 100	 ...options?.headers,
 101	 }
 102	 : options?.headers;
 103	
 104	 const { invocationId } = ctx.genericSend({
 105	 service,
 106	 method,
 107	 parameter: data,
 108	 delay: options?.delay,
 109	 headers,
 110	 key,
 111	 });
 112	
 113	 return invocationId.then(invocationId => ({
 114	 invocationId,
 115	 }));
 116	 },
 117	 call<T>(...args: readonly any[]): RestatePromise<T> {
 118	 const [key, { service, method, data, deserializeReturn }, options] =
 119	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 120	
 121	 const headers = config?.server?.propagateIncomingHeaders
 122	 ? {
 123	 ...propagateRequestHeaders(),
 124	 ...options?.headers,
 125	 }
 126	 : options?.headers;
 127	
 128	 return ctx
 129	 .genericCall({
 130	 service,
 131	 method,
 132	 parameter: data,
 133	 headers,
 134	 key,
 135	 outputSerde: restate.serde.binary,
 136	 })
 137	 .map((value, failure) => {
 138	 if (value) {
 139	 return decodeRestateServiceMethodResponse(value, deserializeReturn);
 140	 }
 141	
 142	 if (
 143	 failure instanceof restate.TerminalError &&
 144	 failure.code === CUSTOM_TERMINAL_ERROR_CODE
 145	 ) {
 146	 deserializeBSONAndThrowCustomTerminalError(failure.message);
 147	 }
 148	
 149	 throw failure;
 150	 });
 151	 },
 152	 };
 153	}
