 66	 @eventDispatcher.listen(onServerMainBootstrap)
 67	 async bootstrap() {
 68	 const services: restate.EndpointOptions['services'] = [];
 69	
 70	 for (const object of this.module.objects) {
 71	 const handlers = this.createObjectHandlers(object);
 72	 services.push(
 73	 restate.object({
 74	 name: object.metadata.name,
 75	 handlers,
 76	 options: object.metadata.options,
 77	 }),
 78	 );
 79	 }
 80	
 81	 for (const service of this.module.services) {
 82	 const handlers = this.createServiceHandlers(service);
 83	 services.push(
 84	 restate.service({
 85	 name: service.metadata.name,
 86	 handlers,
 87	 options: service.metadata.options,
 88	 }),
 89	 );
 90	 }
 91	
 92	 for (const saga of this.module.sagas) {
 93	 const handlers = this.createSagaHandlers(saga);
 94	 services.push(
 95	 restate.workflow({
 96	 name: saga.metadata.name,
 97	 handlers,
 98	 options: saga.metadata.options,
 99	 }),
 100	 );
 101	 }
 102	
 103	 const handler = restate.createEndpointHandler({
 104	 services,
 105	 defaultServiceOptions: {},
 106	 logger: (
 107	 params: LogMetadata,
 108	 message?: any,
 109	 ...optionalParams: any[]
 110	 ) => {
 111	 if (params.replaying) return;
 112	 if (params.context) {
 113	 this.logger.data(params.context);
 114	 }
 115	 if (params.level === 'trace') return;
 116	 this.logger[params.level](message, ...optionalParams);
 117	 },
 118	 });
 119	
 120	 await new Promise<void>(resolve => {
 121	 this.http2Server = createServer(handler);
 122	 this.http2Server.listen(this.module.config.server?.port!, resolve);
 123	 });
