 23	
 24	export function createServiceContext(
 25	 ctx: restate.Context,
 26	 config?: RestateConfig,
 27	): RestateServiceContext {
 28	 function propagateRequestHeaders(): Record<string, string> {
 29	 const entries = Object.entries(ctx.request().headers);
 30	 if (config?.server?.propagateIncomingHeaders) {
 31	 return Object.fromEntries(
 32	 entries.filter(([key]) =>
 33	 config?.server?.propagateIncomingHeaders!.includes(key),
 34	 ),
 35	 );
 36	 }
 37	 return Object.fromEntries(entries);
 38	 }
...
 117	 call<T>(...args: readonly any[]): RestatePromise<T> {
 118	 const [key, { service, method, data, deserializeReturn }, options] =
 119	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 120	
 121	 const headers = config?.server?.propagateIncomingHeaders
 122	 ? {
 123	 ...propagateRequestHeaders(),
 124	 ...options?.headers,
 125	 }
 126	 : options?.headers;
 127	
 128	 return ctx
 129	 .genericCall({
 130	 service,
 131	 method,
 132	 parameter: data,
 133	 headers,
 134	 key,
 135	 outputSerde: restate.serde.binary,
 136	 })
 137	 .map((value, failure) => {
 138	 if (value) {
 139	 return decodeRestateServiceMethodResponse(value, deserializeReturn);
 140	 }
 141	
 142	 if (
 143	 failure instanceof restate.TerminalError &&
 144	 failure.code === CUSTOM_TERMINAL_ERROR_CODE
 145	 ) {
 146	 deserializeBSONAndThrowCustomTerminalError(failure.message);
 147	 }
 148	
 149	 throw failure;
 150	 });
 151	 },
 152	 };
 153	}
