 151	 private async registerEventHandlers(config: RestatePubSubConfig) {
 152	 let handlers: EventHandlers = [];
 153	 for (const { metadata } of this.module.services) {
 154	 for (const handler of metadata.handlers) {
 155	 if (handler.event) {
 156	 handlers = [
 157	 ...handlers,
 158	 {
 159	 service: metadata.name,
 160	 method: handler.name,
 161	 eventName: getTypeName(handler.event.type),
 162	 eventVersion: getTypeHash(handler.event.type),
 163	 },
 164	 ];
 165	 }
 166	 }
 167	 }
 168	 if (handlers.length) {
 169	 const eventStore = this.injectorContext.get<EventStoreApi>();
 170	 const client = this.injectorContext.get(RestateIngressClient);
 171	 // TODO: remove old handlers
 172	 await client.send(config.cluster!, eventStore.registerHandlers(handlers));
 173	 }
 174	 }
