 181	 override processController(
 182	 module: AppModule<any>,
 183	 { controller }: ControllerConfig,
 184	 ) {
 185	 if (!controller) return;
 186	
 187	 const serviceMetadata = getRestateServiceMetadata(controller);
 188	 if (serviceMetadata) {
 189	 this.addService(module, controller, serviceMetadata);
 190	 } else {
 191	 const objectMetadata = getRestateObjectMetadata(controller);
 192	 if (objectMetadata) {
 193	 this.addObject(module, controller, objectMetadata);
 194	 } else {
 195	 const sagaMetadata = getRestateSagaMetadata(controller);
 196	 if (sagaMetadata) {
 197	 this.addSaga(module, controller, sagaMetadata);
 198	 } else {
 199	 return;
 200	 }
 201	 }
 202	 }
 203	
 204	 if (!this.config.server) {
 205	 throw new Error('Restate server config is missing');
 206	 }
 207	
 208	 this.addDeps(controller);
 209	
 210	 if (!module.isProvided(controller)) {
 211	 module.addProvider({ provide: controller, scope: SCOPE });
 212	 }
