 149	 onDecorator(classType: ClassType) {
 150	 Object.assign(this.t, { classType });
 151	 }
 152	
 153	 addHandler(action: RestateHandlerMetadata) {
 154	 this.t.handlers.add(action);
 155	 }
 156	
 157	 saga<T extends RestateSaga<string, any>>(type?: ReceiveType<T>) {
 158	 type = resolveReceiveType(type);
 159	 const name = getRestateClassName(type);
 160	 const deserializeData = getSagaDataDeserializer(type);
 161	 const serializeData = getSagaDataSerializer(type);
 162	 Object.assign(this.t, {
 163	 name,
 164	 type,
 165	 deserializeData,
 166	 serializeData,
 167	 });
 168	 }
 169	
 170	 middleware(...middlewares: ClassType<RestateMiddleware>[]) {
 171	 this.t.middlewares.push(...middlewares);
 172	 }
 173	}
