 192	export class RestateHandlerMetadata<T = readonly unknown[]> {
 193	 readonly name: string;
 194	 readonly classType: ClassType;
 195	 readonly returnType: Type;
 196	 readonly argsType: TypeTuple;
 197	 readonly serializeReturn: BSONSerializer;
 198	 readonly deserializeArgs: BSONDeserializer<T>;
 199	 readonly shared?: boolean;
 200	 readonly exclusive?: boolean;
 201	 readonly kafka?: RestateKafkaHandlerMetadata;
 202	 readonly event?: RestateEventHandlerMetadata;
 203	 readonly options?: RestateHandlerOptions;
 204	 readonly middlewares: ClassType<RestateMiddleware>[] = [];
 205	}
 206	
 207	export class RestateHandlerDecorator {
 208	 t = new RestateHandlerMetadata();
 209	
 210	 onDecorator(classType: ClassType, property: string | undefined) {
 211	 if (!property) return;
 212	
 213	 const reflectionClass = ReflectionClass.from(classType);
 214	 const reflectionMethod = reflectionClass.getMethod(property);
 215	
 216	 const returnType =
 217	 getUnwrappedReflectionFunctionReturnType(reflectionMethod);
 218	 const serializeReturn = getResponseDataSerializer(returnType);
 219	
 220	 const argsType = getReflectionFunctionArgsType(reflectionMethod);
 221	 const deserializeArgs =
 222	 this.t.deserializeArgs || getBSONDeserializer(undefined, argsType);
 223	
 224	 if (this.t.event) {
 225	 if (argsType.types.length !== 1) {
 226	 throw new Error('Event handler must have exactly one argument');
 227	 }
 228	 if (!isSameType(this.t.event.type, argsType.types[0].type)) {
 229	 throw new Error(
 230	 `Event handler argument type ${stringifyType(
 231	 argsType.types[0].type,
 232	 )} does not match event type ${stringifyType(this.t.event.type)}`,
 233	 );
 234	 }
 235	 }
 236	
 237	 Object.assign(this.t, {
 238	 name: property,
 239	 classType,
 240	 returnType,
 241	 serializeReturn,
 242	 argsType,
 243	 deserializeArgs,
 244	 });
 245	
 246	 restateObjectDecorator.addHandler(this.t)(classType);
 247	 restateServiceDecorator.addHandler(this.t)(classType);
 248	 restateSagaDecorator.addHandler(this.t)(classType);
 249	 }
 250	
 251	 handler(options?: RestateHandlerOptions) {
 252	 Object.assign(this.t, { options });
 253	 }
 254	
 255	 event<T>(stream?: string, type?: ReceiveType<T>) {
 256	 type = resolveReceiveType(type);
 257	 const deserialize = getBSONDeserializer(undefined, type);
 258	 Object.assign(this.t, {
 259	 event: { type, stream },
 260	 deserializeArgs: (bson: Uint8Array) => [deserialize(bson)],
 261	 });
 262	 }
 263	
 264	 kafka<T extends RestateKafkaTopic<string, any[]>>(
 265	 options?: Record<string, string>,
 266	 type?: ReceiveType<T>,
 267	 ) {
 268	 type = resolveReceiveType(type);
 269	
 270	 const topic = getRestateKafkaTopicSource(type);
 271	 assertValidKafkaTopicName(topic);
 272	
 273	 const argsType = getRestateKafkaTopicArgsType(type);
 274	 if (!isSameType(argsType, this.t.argsType)) {
 275	 throw new Error(
 276	 `Handler "${this.t.name}" parameters ${stringifyType(this.t.argsType)} does not match Kafka topic "${topic}" arguments ${stringifyType(argsType)}`,
 277	 );
 278	 }
 279	
 280	 options = { 'allow.auto.create.topics': 'true', ...options };
 281	 Object.assign(this.t, {
 282	 kafka: { topic, argsType, options } satisfies RestateKafkaHandlerMetadata,
 283	 });
 284	 }
 285	
 286	 // This only applies to workflows & objects
 287	 shared() {
 288	 if (this.t.exclusive) {
 289	 throw new Error('Handler is already marked as exclusive');
 290	 }
 291	 Object.assign(this.t, { shared: true });
 292	 }
 293	
 294	 // This only applies to objects
 295	 exclusive() {
 296	 if (this.t.shared) {
 297	 throw new Error('Handler is already marked as shared');
 298	 }
 299	 Object.assign(this.t, { exclusive: true });
 300	 }
 301	
 302	 middleware(...middlewares: ClassType<RestateMiddleware>[]) {
 303	 this.t.middlewares.push(...middlewares);
 304	 }
 305	}
