 307	type RestateClassFluidDecorator<T, D extends Function> = {
 308	 [K in keyof T]: K extends 'service'
 309	 ? <For extends RestateService<string, any>>(
 310	 options?: ServiceOptions,
 311	 type?: ReceiveType<For>,
 312	 ) => D & RestateClassFluidDecorator<T, D>
 313	 : K extends 'object'
 314	 ? <For extends RestateObject<string, any>>(
 315	 options?: ObjectOptions,
 316	 type?: ReceiveType<For>,
 317	 ) => D & RestateClassFluidDecorator<T, D>
 318	 : K extends 'saga'
 319	 ? <For extends RestateSaga<string, any>>(
 320	 type?: ReceiveType<For>,
 321	 ) => D & RestateClassFluidDecorator<T, D>
 322	 : T[K] extends (...args: infer K) => any
 323	 ? (...args: K) => D & RestateClassFluidDecorator<T, D>
 324	 : D &
 325	 RestateClassFluidDecorator<T, D> & {
 326	 _data: ExtractApiDataType<T>;
 327	 };
 328	};
