 201	 readonly kafka?: RestateKafkaHandlerMetadata;
 202	 readonly event?: RestateEventHandlerMetadata;
 203	 readonly options?: RestateHandlerOptions;
 204	 readonly middlewares: ClassType<RestateMiddleware>[] = [];
 205	}
