 331	 ExtractClass<typeof RestateServiceDecorator>,
 332	 ClassDecoratorFn
 333	> &
 334	 DecoratorAndFetchSignature<typeof RestateServiceDecorator, ClassDecoratorFn>;
 335	
 336	export const restateServiceDecorator = createClassDecoratorContext(
 337	 RestateServiceDecorator,
 338	) as RestateServiceDecoratorResult;
