 360	type RestateMerge<U> = {
 361	 [K in keyof U]: K extends 'service'
 362	 ? <For extends RestateService<string, any>>(
 363	 options?: ServiceOptions,
 364	 type?: ReceiveType<For>,
 365	 ) => (PropertyDecoratorFn | ClassDecoratorFn) & U
 366	 : K extends 'object'
 367	 ? <For extends RestateObject<string, any>>(
 368	 options?: ObjectOptions,
 369	 type?: ReceiveType<For>,
 370	 ) => (PropertyDecoratorFn | ClassDecoratorFn) & U
 371	 : K extends 'saga'
 372	 ? <For extends RestateSaga<string, any>>(
 373	 type?: ReceiveType<For>,
 374	 ) => (PropertyDecoratorFn | ClassDecoratorFn) & U
 375	 : U[K] extends (...a: infer A) => infer R
 376	 ? R extends DualDecorator
 377	 ? (...a: A) => (PropertyDecoratorFn | ClassDecoratorFn) & R & U
 378	 : (...a: A) => R
 379	 : never;
 380	};
 381	
 382	type MergedRestate<T extends any[]> = RestateMerge<
 383	 Omit<UnionToIntersection<T[number]>, '_fetch' | 't'>
 384	>;
 385	
 386	export const restateHandlerDecorator: PropertyDecoratorResult<
 387	 typeof RestateHandlerDecorator
 388	> = createPropertyDecoratorContext(RestateHandlerDecorator);
 389	
 390	export type MergedRestateDecorator = Omit<
 391	 MergedRestate<
 392	 [
 393	 typeof restateObjectDecorator,
 394	 typeof restateServiceDecorator,
 395	 typeof restateSagaDecorator,
 396	 typeof restateHandlerDecorator,
 397	 ]
 398	 >,
 399	 'addHandler'
 400	>;
