 232	export function decodeRestateServiceMethodResponse<T>(
 233	 response: Uint8Array,
 234	 deserialize: BSONDeserializer<T>,
 235	): T {
 236	 const internalResponse = deserializeRestateHandlerResponse(response);
 237	 return internalResponse.data
 238	 ? deserialize(internalResponse.data)
 239	 : (undefined as T);
 240	}
