 247	
 248	export function success<T>(
 249	 reply?: T,
 250	 type?: ReceiveType<T>,
 251	): RestateHandlerResponse {
 252	 if (reply) {
 253	 type = resolveReceiveType(type);
 254	 return {
 255	 success: true,
 256	 data: serializeResponseData(reply, type),
 257	 };
 258	 }
 259	
 260	 return { success: true };
 261	}
 262	
 263	export function failure<T>(
 264	 reply?: T,
 265	 type?: ReceiveType<T>,
 266	): RestateHandlerResponse {
 267	 if (reply) {
 268	 type = resolveReceiveType(type);
 269	 return {
 270	 success: false,
 271	 data: serializeResponseData(reply, type),
 272	 };
 273	 }
 274	
 275	 return { success: false };
 276	}
 277	
 278	export function waitUntil(
 279	 predicate: () => boolean,
 280	 timeout: number = 1000,
 281	): Promise<void> {
 282	 return new Promise(async (resolve, reject) => {
 283	 let wait = true;
 284	
 285	 setTimeout(() => {
 286	 wait = false;
 287	 reject(new Error(`Timeout ${timeout}ms exceeded`));
 288	 }, timeout);
 289	
 290	 while (wait) {
 291	 if (predicate()) {
 292	 wait = false;
 293	 resolve();
 294	 }
 295	 await sleep(0);
 296	 }
 297	 });
 298	}
