 59	export class RestateSagaClient<Data> {
 60	 private readonly serializeData: BSONSerializer;
 61	 private readonly deserializeData: BSONDeserializer<Data>;
 62	 private readonly serviceName: string;
 63	
 64	 constructor(
 65	 private readonly opts: RestateIngressClientOptions,
 66	 private readonly type: Type,
 67	 ) {
 68	 this.serializeData = getSagaDataSerializer(this.type);
 69	 this.deserializeData = getSagaDataDeserializer<Data>(this.type);
 70	 this.serviceName = getRestateClassName(this.type);
 71	 }
 72	
 73	 async state(id: string): Promise<SagaState<Data>> {
 74	 const url = `${this.opts.url}/${this.serviceName}/${id}/state`;
 75	
 76	 const headers = new Headers({
 77	 ...this.opts.headers,
 78	 'content-type': 'application/octet-stream',
 79	 accept: 'application/octet-stream',
 80	 });
 81	
 82	 const response = await fetch(url, {
 83	 method: 'POST',
 84	 headers,
 85	 });
 86	
 87	 if (!response.ok) {
 88	 throw new Error('Missing saga state');
 89	 }
 90	
 91	 const state = deserializeResponseData<SagaState>(
 92	 new Uint8Array(await response.arrayBuffer()),
 93	 );
 94	
 95	 return {
 96	 sagaData: this.deserializeData(state.sagaData),
 97	 currentState: state.currentState,
 98	 };
 99	 }
 100	
 101	 async start(id: string, data: Data): Promise<InvocationHandle> {
 102	 const url = `${this.opts.url}/${this.serviceName}/${id}/run/send`;
 103	
 104	 const headers = new Headers({
 105	 ...this.opts.headers,
 106	 'content-type': 'application/octet-stream',
 107	 accept: 'application/json',
 108	 });
 109	
 110	 const response = await fetch(url, {
 111	 method: 'POST',
 112	 headers,
 113	 body: this.serializeData(data),
 114	 });
 115	
 116	 return (await response.json()) as InvocationHandle;
 117	 }
 118	}
