 143	 call<R, A extends any[]>(
 144	 request: RestateServiceHandlerRequest<R, A>,
 145	 options?: RestateCallOptions,
 146	 ): Promise<R>;
 147	 async call<R>(...args: readonly any[]): Promise<R> {
 148	 const [key, { service, method, data, deserializeReturn }, options] =
 149	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 150	
 151	 const url = new URL(
 152	 key
 153	 ? `${this.opts.url}/${service}/${key}/${method}`
 154	 : `${this.opts.url}/${service}/${method}`,
 155	 );
 156	
 157	 const headers = new Headers({
 158	 ...this.opts.headers,
 159	 ...options?.headers,
 160	 'content-type': 'application/octet-stream',
 161	 accept: 'application/octet-stream',
 162	 });
 163	 if (options?.idempotencyKey) {
 164	 headers.set('idempotency-key', options.idempotencyKey);
 165	 }
 166	
 167	 const response = await fetch(url, {
 168	 method: 'POST',
 169	 headers,
 170	 body: data,
 171	 } as RequestInit);
 172	
 173	 if (!response.ok) {
 174	 if (response.status === CUSTOM_TERMINAL_ERROR_CODE) {
 175	 const failure = (await response.json()) as { message: string };
 176	 deserializeBSONAndThrowCustomTerminalError(failure.message);
 177	 }
 178	 const { code, message } =
 179	 (await response.json()) as RestateApiResponseError;
 180	 throw new RestateApiError(code, message);
 181	 }
 182	
 183	 const result = new Uint8Array(await response.arrayBuffer());
 184	
 185	 return decodeRestateServiceMethodResponse(result, deserializeReturn);
 186	 }
 187	
 188	 send(
 189	 key: string,
 190	 request: Omit<RestateObjectHandlerRequest, 'deserializeReturn'>,
 191	 options?: RestateSendOptions,
 192	 ): Promise<InvocationHandle>;
 193	 send(
 194	 request: Omit<RestateServiceHandlerRequest, 'deserializeReturn'>,
 195	 options?: RestateSendOptions,
 196	 ): Promise<InvocationHandle>;
 197	 async send(...args: readonly any[]): Promise<InvocationHandle> {
 198	 const [key, { service, method, data }, options] =
 199	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 200	
 201	 const url = new URL(
 202	 key
 203	 ? `${this.opts.url}/${service}/${key}/${method}/send`
 204	 : `${this.opts.url}/${service}/${method}/send`,
 205	 );
 206	 if (options?.delay) {
 207	 url.searchParams.set('delay', options.delay);
 208	 }
 209	
 210	 const headers = new Headers({
 211	 ...this.opts.headers,
 212	 ...options?.headers,
 213	 'content-type': 'application/octet-stream',
 214	 accept: 'application/octet-stream',
 215	 });
 216	 if (options?.idempotencyKey) {
 217	 headers.set('idempotency-key', options.idempotencyKey);
 218	 }
 219	
 220	 const response = await fetch(url, {
 221	 method: 'POST',
 222	 headers,
 223	 body: data,
 224	 } as RequestInit);
 225	 if (!response.ok) {
 226	 const { message } = (await response.json()) as RestateApiResponseError;
 227	 throw new Error(message);
 228	 }
 229	
 230	 return (await response.json()) as InvocationHandle;
 231	 }
 232	}
