 17	 async subscribe<T>(
 18	 callback: (event: T) => Promise<unknown> | unknown,
 19	 options?: SubscribeOptions,
 20	 type?: ReceiveType<T>,
 21	 ): Promise<() => Promise<void>> {
 22	 type = resolveReceiveType(type);
 23	 const types = type.kind === ReflectionKind.union ? type.types : [type];
 24	 for (const type of types) {
 25	 if (type.kind !== ReflectionKind.class) {
 26	 throw new Error('Only classes are supported');
 27	 }
 28	 }
 29	 const events = new Map(
 30	 types.map(type => [
 31	 this.config.eventVersioning
 32	 ? `${getTypeName(type)}:${getTypeHash(type)}`
 33	 : getTypeName(type),
 34	 type,
 35	 ]),
 36	 );
 37	 const stream = options?.stream || this.config.defaultStream;
 38	 const eventSource = new EventSource(
 39	 `${this.config.sse!.url}/sse/${this.config.cluster}/${stream}/${events.keys().toArray().join(',')}`,
 40	 );
 41	 for (const [id, type] of events.entries()) {
 42	 eventSource.addEventListener(id, event => {
 43	 callback(
 44	 deserializeBSON(
 45	 base64ToUint8Array(event.data),
 46	 undefined,
 47	 undefined,
 48	 type,
 49	 ),
 50	 );
 51	 });
 52	 }
 53	
 54	 return async () => eventSource.close();
 55	 }
