 1	import { AppModule, ControllerConfig, createModuleClass } from '@deepkit/app';
 2	import { ClassType } from '@deepkit/core';
 3	
 4	import { RestateAdminClient } from './client/restate-admin-client.js';
 5	import { RestateIngressClient } from './client/restate-ingress-client.js';
 6	import { RestateConfig } from './config.js';
 7	import { InjectorServices } from './services.js';
 8	import { InjectorObjects } from './objects.js';
 9	import { InjectorSagas } from './sagas.js';
 10	import { RestateServer } from './restate-server.js';
 11	import { RestatePubSubModule } from './event/module.js';
 12	import {
 13	 RestateClassMetadata,
 14	 RestateObjectMetadata,
 15	 RestateSagaMetadata,
 16	 RestateServiceMetadata,
 17	} from './decorator.js';
 18	import {
 19	 restateObjectContextType,
 20	 restateSagaContextType,
 21	 restateServiceContextType,
 22	 SCOPE,
 23	 restateClientType,
 24	 restateSharedContextType,
 25	} from './types.js';
 26	import { makeInterfaceProxy, getRestateClassDeps } from './utils.js';
 27	import {
 28	 getRestateObjectMetadata,
 29	 getRestateSagaMetadata,
 30	 getRestateServiceMetadata,
 31	} from './metadata.js';
 32	import { RestateMiddleware } from './middleware.js';
 33	
 34	export class RestateModule extends createModuleClass({
 35	 config: RestateConfig,
 36	 forRoot: true,
 37	}) {
 38	 readonly services = new InjectorServices();
 39	 readonly objects = new InjectorObjects();
 40	 readonly sagas = new InjectorSagas();
 41	 readonly globalMiddlewares: ClassType<RestateMiddleware>[] = [];
 42	
 43	 override process() {
 44	 if (this.config.ingress) {
 45	 this.addProvider({
 46	 provide: RestateIngressClient,
 47	 useValue: new RestateIngressClient(this.config.ingress),
 48	 });
 49	 } else {
 50	 this.addProvider({
 51	 provide: RestateIngressClient,
 52	 useFactory() {
 53	 throw new Error('Restate ingress config is missing');
 54	 },
 55	 });
 56	 }
 57	 this.addProvider({
 58	 provide: restateClientType,
 59	 useExisting: RestateIngressClient,
 60	 });
 61	
 62	 if (this.config.pubsub) {
 63	 this.addImport(new RestatePubSubModule(this.config.pubsub));
 64	 }
 65	
 66	 if (this.config.admin) {
 67	 this.addProvider({
 68	 provide: RestateAdminClient,
 69	 useValue: new RestateAdminClient(this.config.admin),
 70	 });
 71	 }
...
 137	
 138	 private addService(
 139	 module: AppModule<any>,
 140	 classType: ClassType,
 141	 metadata: RestateServiceMetadata,
 142	 ): void {
 143	 this.services.add({ classType, module, metadata });
 144	 this.addClassMetadataMiddleware(metadata);
 145	 }
 146	
 147	 private addObject(
 148	 module: AppModule<any>,
 149	 classType: ClassType,
 150	 metadata: RestateObjectMetadata,
 151	 ): void {
 152	 this.objects.add({ classType, module, metadata });
 153	 this.addClassMetadataMiddleware(metadata);
 154	 }
 155	
 156	 private addSaga(
 157	 module: AppModule<any>,
 158	 classType: ClassType,
 159	 metadata: RestateSagaMetadata,
 160	 ): void {
 161	 this.sagas.add({ classType, module, metadata });
 162	 this.addClassMetadataMiddleware(metadata);
 163	 }
 164	
 165	 private addDeps(classType: ClassType): void {
 166	 const restateServiceDeps = getRestateClassDeps(classType);
 167	
 168	 for (const dependency of restateServiceDeps) {
 169	 if (!this.isProvided(dependency)) {
 170	 this.addProvider({
 171	 provide: dependency,
 172	 scope: SCOPE,
 173	 useValue: makeInterfaceProxy(dependency),
 174	 });
 175	 }
 176	 }
 177	 }
 178	
 179	 override processController(
 180	 module: AppModule<any>,
 181	 { controller }: ControllerConfig,
 182	 ) {
 183	 if (!controller) return;
 184	
 185	 const serviceMetadata = getRestateServiceMetadata(controller);
 186	 if (serviceMetadata) {
 187	 this.addService(module, controller, serviceMetadata);
 188	 } else {
 189	 const objectMetadata = getRestateObjectMetadata(controller);
 190	 if (objectMetadata) {
 191	 this.addObject(module, controller, objectMetadata);
 192	 } else {
 193	 const sagaMetadata = getRestateSagaMetadata(controller);
 194	 if (sagaMetadata) {
 195	 this.addSaga(module, controller, sagaMetadata);
 196	 } else {
 197	 return;
 198	 }
 199	 }
 200	 }
 201	
 202	 if (!this.config.server) {
 203	 throw new Error('Restate server config is missing');
 204	 }
 205	
 206	 this.addDeps(controller);
 207	
 208	 if (!module.isProvided(controller)) {
 209	 module.addProvider({ provide: controller, scope: SCOPE });
 210	 }
