 20	 async create({
 21	 source,
 22	 sink,
 23	 options,
 24	 }: KafkaSubscriptionsCreateOptions): Promise<Response> {
 25	 const url = `${this.client.opts.url}/subscriptions`;
 26	
 27	 const response = await fetch(url, {
 28	 method: 'POST',
 29	 body: JSON.stringify({
 30	 source,
 31	 sink,
 32	 options,
 33	 }),
 34	 headers: {
 35	 'content-type': 'application/json',
 36	 },
 37	 });
 38	 if (response.status !== 201) {
 39	 throw new Error(await response.text());
 40	 }
 41	 return response;
 42	 }
 43	}
