 55	
 56	 async create(uri: string): Promise<any> {
 57	 const response = await fetch(`${this.client.opts.url}/deployments`, {
 58	 method: 'POST',
 59	 headers: {
 60	 'Content-Type': 'application/json',
 61	 },
 62	 body: JSON.stringify({ uri }),
 63	 });
 64	 if (!response.ok) {
 65	 throw new Error(await response.text());
 66	 }
 67	 return await response.json();
 68	 }
 69	}
