 49	export function getRestateClassDeps(classType: ClassType): readonly Type[] {
 50	 const serviceType = reflect(classType);
 51	 const ctorParameters = getClassConstructorParameters(serviceType);
 52	
 53	 return ctorParameters
 54	 .filter(
 55	 parameter =>
 56	 isRestateServiceType(parameter.type) ||
 57	 isRestateObjectType(parameter.type),
 58	 )
 59	 .map(parameter => parameter.type);
 60	}
 61	
 62	export function getClassConstructorParameters(
 63	 type: Type,
 64	): readonly TypeParameter[] {
 65	 assertType(type, ReflectionKind.class);
 66	
 67	 const constructor = type.types.find(
 68	 type => type.kind === ReflectionKind.method && type.name === 'constructor',
 69	 );
 70	
 71	 return constructor?.kind === ReflectionKind.method
 72	 ? constructor.parameters
 73	 : [];
 74	}
 75	
 76	export function isRestateServiceType(type: Type): boolean {
 77	 if (type.kind === ReflectionKind.class) return false;
 78	 if (
 79	 type.typeName !== restateServiceType.typeName &&
 80	 type.originTypes?.[0].typeName !== restateServiceType.typeName
 81	 ) {
 82	 return false;
 83	 }
 84	 return isExtendable(type, restateServiceType);
 85	}
...
 110	
 111	export function unwrapType(type: Type): Type {
 112	 switch (type.kind) {
 113	 case ReflectionKind.promise:
 114	 return type.type;
 115	
 116	 case ReflectionKind.void:
 117	 return type;
 118	
 119	 default:
 120	 return type;
 121	 }
 122	}
 123	
 124	export function getTypeArgument(type: Type, index: number): Type | undefined {
 125	 return (
 126	 type.typeArguments?.[index] || type.originTypes?.[0].typeArguments?.[index]
 127	 );
 128	}
