 130	interface ClassProxyMethod<T> {
 131	 readonly serializeArgs: BSONSerializer;
 132	 readonly deserializeReturn: BSONDeserializer<T>;
 133	}
...
 160	
 161	export function makeInterfaceProxy<
 162	 T extends RestateService<string, any> | RestateObject<string, any>,
 163	>(type?: ReceiveType<T>): T {
 164	 type = resolveReceiveType(type);
 165	
 166	 const service = getRestateClassName(type);
 167	
 168	 const classType = getTypeArgument(type, 1);
 169	
 170	 const reflectionClass = ReflectionClass.from(classType);
 171	
 172	 const methods: Record<string, ClassProxyMethod<unknown>> = {};
 173	
 174	 return new Proxy(
 175	 {},
 176	 {
 177	 get(target: any, method: string) {
 178	 if (!methods[method]) {
 179	 const reflectionMethod = reflectionClass.getMethod(method);
 180	
 181	 const argsType = getReflectionFunctionArgsType(reflectionMethod);
 182	 const serializeArgs = getBSONSerializer(undefined, argsType);
 183	
 184	 const returnType =
 185	 getUnwrappedReflectionFunctionReturnType(reflectionMethod);
 186	 const deserializeReturn = getResponseDataDeserializer(returnType);
 187	
 188	 methods[method] = { serializeArgs, deserializeReturn };
 189	 }
 190	 const { serializeArgs, deserializeReturn } = methods[method];
 191	
 192	 return (...args: readonly unknown[]): RestateHandlerRequest => {
 193	 const data = serializeArgs(args);
 194	 return {
 195	 service,
 196	 method,
 197	 data,
 198	 deserializeReturn,
 199	 };
 200	 };
 201	 },
 202	 },
 203	 );
 204	}
 205	
 206	export function provideRestateServiceProxy<
 207	 T extends RestateService<string, any>,
 208	>(type?: ReceiveType<T>): FactoryProvider<T> {
 209	 type = resolveReceiveType(type);
 210	 return {
 211	 provide: type,
 212	 useFactory: () => makeInterfaceProxy<T>(type),
 213	 };
 214	}
 215	
 216	export function provideRestateObjectProxy<T extends RestateObject<string, any>>(
 217	 type?: ReceiveType<T>,
 218	): FactoryProvider<T> {
 219	 type = resolveReceiveType(type);
 220	 return {
 221	 provide: type,
 222	 useFactory: () => makeInterfaceProxy<T>(type),
 223	 };
 224	}
