 242	export function assertValidKafkaTopicName(topicName: string): void {
 243	 if (!/^[a-zA-Z0-9._-]+$/.test(topicName)) {
 244	 throw new Error(
 245	 `Invalid topic name validation pattern ^[a-zA-Z0-9._-]+$ failed for ${topicName}`,
 246	 );
 247	 }
 248	}
 249	
 250	export function success<T>(
 251	 reply?: T,
 252	 type?: ReceiveType<T>,
 253	): RestateHandlerResponse {
 254	 if (reply) {
 255	 type = resolveReceiveType(type);
 256	 return {
 257	 success: true,
 258	 data: serializeResponseData(reply, type),
 259	 };
 260	 }
 261	
 262	 return { success: true };
 263	}
 264	
 265	export function failure<T>(
 266	 reply?: T,
 267	 type?: ReceiveType<T>,
 268	): RestateHandlerResponse {
 269	 if (reply) {
 270	 type = resolveReceiveType(type);
 271	 return {
 272	 success: false,
 273	 data: serializeResponseData(reply, type),
 274	 };
 275	 }
 276	
 277	 return { success: false };
 278	}
 279	
 280	export function waitUntil(
 281	 predicate: () => boolean,
 282	 timeout: number = 1000,
 283	): Promise<void> {
 284	 return new Promise(async (resolve, reject) => {
 285	 let wait = true;
 286	
 287	 setTimeout(() => {
 288	 wait = false;
 289	 reject(new Error(`Timeout ${timeout}ms exceeded`));
 290	 }, timeout);
 291	
 292	 while (wait) {
 293	 if (predicate()) {
 294	 wait = false;
 295	 resolve();
 296	 }
 297	 await sleep(0);
 298	 }
 299	 });
 300	}
