 179	 override processController(
 180	 module: AppModule<any>,
 181	 { controller }: ControllerConfig,
 182	 ) {
 183	 if (!controller) return;
 184	
 185	 const serviceMetadata = getRestateServiceMetadata(controller);
 186	 if (serviceMetadata) {
 187	 this.addService(module, controller, serviceMetadata);
 188	 } else {
 189	 const objectMetadata = getRestateObjectMetadata(controller);
 190	 if (objectMetadata) {
 191	 this.addObject(module, controller, objectMetadata);
 192	 } else {
 193	 const sagaMetadata = getRestateSagaMetadata(controller);
 194	 if (sagaMetadata) {
 195	 this.addSaga(module, controller, sagaMetadata);
 196	 } else {
 197	 return;
 198	 }
 199	 }
 200	 }
 201	
 202	 if (!this.config.server) {
 203	 throw new Error('Restate server config is missing');
 204	 }
 205	
 206	 this.addDeps(controller);
 207	
 208	 if (!module.isProvided(controller)) {
 209	 module.addProvider({ provide: controller, scope: SCOPE });
 210	 }
