 47	export function getRestateClassDeps(classType: ClassType): readonly Type[] {
 48	 const serviceType = reflect(classType);
 49	 const ctorParameters = getClassConstructorParameters(serviceType);
 50	
 51	 return ctorParameters
 52	 .filter(
 53	 parameter =>
 54	 isRestateServiceType(parameter.type) ||
 55	 isRestateObjectType(parameter.type),
 56	 )
 57	 .map(parameter => parameter.type);
 58	}
