 132	
 133	export function getReflectionFunctionArgsType(
 134	 reflectionFunction: ReflectionFunction,
 135	): TypeTuple {
 136	 const argsType: TypeTuple = {
 137	 kind: ReflectionKind.tuple,
 138	 types: [],
 139	 };
 140	
 141	 argsType.types = reflectionFunction.parameters.map(
 142	 ({ parameter }) =>
 143	 ({
 144	 ...parameter,
 145	 parent: argsType,
 146	 kind: ReflectionKind.tupleMember,
 147	 }) as TypeTupleMember,
 148	 );
 149	
 150	 return argsType;
 151	}
 152	
 153	export function getUnwrappedReflectionFunctionReturnType(
 154	 reflectionFunction: ReflectionFunction,
 155	): Type {
 156	 return unwrapType(reflectionFunction.getReturnType());
 157	}
