 230	export function decodeRestateServiceMethodResponse<T>(
 231	 response: Uint8Array,
 232	 deserialize: BSONDeserializer<T>,
 233	): T {
 234	 const internalResponse = deserializeRestateHandlerResponse(response);
 235	 return internalResponse.data
 236	 ? deserialize(internalResponse.data)
 237	 : (undefined as T);
 238	}
