 120	export class RestateIngressClient implements RestateClient {
 121	 constructor(private readonly opts: RestateIngressClientOptions) {}
 122	
 123	 service<T extends RestateService<string, any>>(type?: ReceiveType<T>): T {
 124	 return makeInterfaceProxy<T>(type);
 125	 }
 126	
 127	 object<T extends RestateObject<string, any>>(type?: ReceiveType<T>): T {
 128	 return makeInterfaceProxy<T>(type);
 129	 }
 130	
 131	 saga<T extends RestateSaga<string, any>>(
 132	 type?: ReceiveType<T>,
 133	 ): RestateSagaClient<T['data']> {
 134	 type = resolveReceiveType(type);
 135	 return new RestateSagaClient(this.opts, type);
 136	 }
 137	
 138	 call<R, A extends any[]>(
 139	 key: string,
 140	 request: RestateObjectHandlerRequest<R, A>,
 141	 options?: RestateCallOptions,
 142	 ): Promise<R>;
 143	 call<R, A extends any[]>(
 144	 request: RestateServiceHandlerRequest<R, A>,
 145	 options?: RestateCallOptions,
 146	 ): Promise<R>;
