 56	
 57	type RestateHandler<F, T extends RestateHandlerType> = F extends (
 58	 ...args: infer P
 59	) => infer R
 60	 ? (...args: P) => RestateHandlerRequest<Awaited<R>, P, T>
 61	 : never;
 62	
 63	export type RestateObjectHandler<F> = RestateHandler<F, 'object'>;
 64	
 65	export type RestateServiceHandler<F> = RestateHandler<F, 'service'>;
 66	
 67	export type RestateService<Name extends string, Interface> = {
 68	 [Method in keyof Interface as Interface[Method] extends never
 69	 ? never
 70	 : Method]: RestateServiceHandler<Interface[Method]>;
 71	};
 72	
 73	export type RestateObject<Name extends string, Interface> = {
 74	 [Method in keyof Interface as Interface[Method] extends never
 75	 ? never
 76	 : Method]: RestateObjectHandler<Interface[Method]>;
 77	};
