 182	 override processController(
 183	 module: AppModule<any>,
 184	 { controller }: ControllerConfig,
 185	 ) {
 186	 if (!controller) return;
 187	
 188	 const serviceMetadata = getRestateServiceMetadata(controller);
 189	 if (serviceMetadata) {
 190	 this.addService(module, controller, serviceMetadata);
 191	 } else {
 192	 const objectMetadata = getRestateObjectMetadata(controller);
 193	 if (objectMetadata) {
 194	 this.addObject(module, controller, objectMetadata);
 195	 } else {
 196	 const sagaMetadata = getRestateSagaMetadata(controller);
 197	 if (sagaMetadata) {
 198	 this.addSaga(module, controller, sagaMetadata);
 199	 } else {
 200	 return;
 201	 }
 202	 }
 203	 }
 204	
 205	 if (!this.config.server) {
 206	 throw new Error('Restate server config is missing');
 207	 }
 208	
 209	 this.addDeps(controller);
 210	
 211	 if (!module.isProvided(controller)) {
 212	 module.addProvider({ provide: controller, scope: SCOPE });
 213	 }
