 18	 async publish<E extends any[]>(
 19	 events: E,
 20	 options?: PublishOptions,
 21	 ): Promise<InvocationHandle> {
 22	 const eventTypes = events.map(event =>
 23	 resolveRuntimeType(event.constructor),
