 36	 name: getTypeName(type),
 37	 id: fastHash(data),
 38	 data: Array.from(data),
 39	 version,
 40	 };
 41	 });
 42	
 43	 const idempotencyKey = eventsToPublish.map(e => e.id).join('-');
 44	
 45	 const stream = options?.stream || this.module?.config.defaultStream;
 46	 if (!stream) {
 47	 throw new TerminalError('No stream configured');
 48	 }
 49	
 50	 const cluster = options?.cluster || this.module?.config.cluster;
 51	 if (!cluster) {
 52	 throw new TerminalError('No cluster configured');
 53	 }
 54	
 55	 return this.client.send(
 56	 this.processor.process(eventsToPublish, {
 57	 stream,
 58	 cluster,
 59	 sse: options?.sse,
 60	 }),
 61	 {
 62	 delay: options?.delay,
 63	 idempotencyKey,
 64	 },
 65	 );
 66	 }
