 158	 private async registerEventHandlers(config: RestatePubSubConfig) {
 159	 let handlers: EventHandlers = [];
 160	 for (const { metadata } of this.module.services) {
 161	 for (const handler of metadata.handlers) {
 162	 if (handler.event) {
 163	 function addHandler(type: TypeClass | TypeObjectLiteral) {
 164	 handlers = [
 165	 ...handlers,
 166	 {
 167	 service: metadata.name,
 168	 method: handler.name,
 169	 eventName: getTypeName(type),
 170	 eventVersion: getTypeHash(type),
 171	 },
 172	 ];
 173	 }
 174	
 175	 if (handler.event.type.kind === ReflectionKind.union) {
 176	 for (const type of handler.event.type.types) {
 177	 addHandler(type as TypeClass | TypeObjectLiteral);
 178	 }
 179	 } else {
 180	 addHandler(handler.event.type);
 181	 }
 182	 }
 183	 }
 184	 }
 185	 if (handlers.length) {
 186	 const eventStore = this.injectorContext.get<EventStoreApi>();
 187	 const client = this.injectorContext.get(RestateIngressClient);
 188	 // TODO: remove old handlers
 189	 await client.send(config.cluster!, eventStore.registerHandlers(handlers));
 190	 }
 191	 }
