 24	
 25	 @restate.handler()
 26	 async registerHandlers(newHandlers: EventHandlers): Promise<void> {
 27	 const currentHandlers = await this.#getHandlers();
 28	 const allHandlers = new Map<string, EventHandler>();
 29	
 30	 const generateKey = (sub: EventHandler) =>
 31	 `${sub.service}-${sub.method}-${sub.eventName}:${sub.eventVersion}`;
 32	
 33	 currentHandlers.forEach(sub => {
 34	 const key = generateKey(sub);
 35	 allHandlers.set(key, sub);
 36	 });
 37	
 38	 newHandlers.forEach(sub => {
 39	 const key = generateKey(sub);
 40	 allHandlers.set(key, sub);
 41	 });
 42	
 43	 this.ctx.set<EventHandlers>(
 44	 HANDLERS_STATE_KEY,
 45	 allHandlers.values().toArray(),
 46	 );
 47	 }
 48	}
