 17	@restate.service<EventProcessorApi>()
 18	export class RestateEventProcessor implements EventProcessorHandlers {
 19	 constructor(
 20	 private readonly ctx: RestateServiceContext,
 21	 private readonly store: EventStoreApi,
 22	 private readonly sseConfig: RestateSseConfig,
 23	 private readonly config: RestatePubSubServerConfig,
 24	 ) {}
 25	
 26	 @restate.handler()
 27	 async process(
 28	 events: readonly PublishEvent[],
 29	 options?: PublishOptions,
 30	 ): Promise<void> {
 31	 const cluster = options?.cluster || this.config.cluster!;
 32	 const allHandlers = await this.ctx.call(cluster, this.store.getHandlers());
 33	
 34	 for (const event of events) {
 35	 const eventHandlers = allHandlers.filter(
 36	 handler => handler.eventName === event.name,
 37	 );
 38	 for (const handler of eventHandlers) {
 39	 this.ctx.genericSend({
 40	 service: handler.service,
 41	 method: handler.method,
 42	 // TODO: provide stream as second argument
 43	 parameter: new Uint8Array(event.data),
 44	 headers: {
 45	 'x-restate-event': event.name,
 46	 },
 47	 inputSerde: serde.binary,
 48	 idempotencyKey: event.id,
 49	 });
 50	 }
 51	 }
 52	
 53	 if (this.sseConfig.nodes && (options?.sse ?? this.sseConfig.all)) {
 54	 await this.fanOutServerSentEvents(
 55	 cluster,
 56	 options?.stream || this.config.defaultStream!,
 57	 this.sseConfig.nodes,
 58	 events,
 59	 );
 60	 }
 61	 }
 62	
 63	 private async fanOutServerSentEvents(
 64	 cluster: string,
 65	 stream: string,
 66	 nodes: string[],
 67	 events: readonly PublishEvent[],
 68	 ) {
 69	 await RestatePromise.all(
 70	 nodes.map(node =>
 71	 this.ctx.run(
 72	 `fan-out server-sent events to node "${node}"`,
 73	 async () => {
 74	 // TODO: only publish to controllers that do have active subscriptions
 75	 const response = await fetch(
 76	 `http://${node}/sse/${cluster}/${stream}`,
 77	 {
 78	 method: 'POST',
 79	 body: JSON.stringify(events),
 80	 headers: {
 81	 'content-type': 'application/json',
 82	 },
 83	 },
 84	 );
 85	 if (!response.ok) {
 86	 throw new Error(await response.text());
 87	 }
 88	 },
 89	 {
 90	 initialRetryIntervalMillis: 250,
