 24	export function createServiceContext(
 25	 ctx: restate.Context,
 26	 config?: RestateConfig,
 27	): RestateServiceContext {
 28	 return {
 29	 workflowClient: ctx.workflowClient.bind(ctx),
 30	 workflowSendClient: ctx.workflowSendClient.bind(ctx),
 31	 serviceClient: ctx.serviceClient.bind(ctx),
 32	 objectClient: ctx.objectClient.bind(ctx),
 33	 objectSendClient: ctx.objectSendClient.bind(ctx),
 34	 serviceSendClient: ctx.serviceSendClient.bind(ctx),
 35	 rand: ctx.rand,
 36	 date: ctx.date,
 37	 console: ctx.console,
 38	 request: ctx.request.bind(ctx),
 39	 sleep: ctx.sleep.bind(ctx),
 40	 rejectAwakeable: ctx.rejectAwakeable.bind(ctx),
 41	 genericCall: ctx.genericCall.bind(ctx),
 42	 genericSend: ctx.genericSend.bind(ctx),
 43	 cancel: ctx.cancel.bind(ctx),
 44	 attach<T>(
 45	 invocationId: InvocationId,
 46	 type?: ReceiveType<T>,
 47	 ): RestatePromise<T> {
 48	 const serde = createBSONSerde(type);
 49	 return ctx.attach(invocationId, serde);
 50	 },
 51	 resolveAwakeable<T>(id: string, payload?: T, type?: ReceiveType<T>) {
 52	 const serde = createBSONSerde(type);
 53	 ctx.resolveAwakeable(id, payload, serde);
 54	 },
 55	 awakeable<T>(type?: ReceiveType<T>): RestateAwakeable<T> {
 56	 const serde = createBSONSerde<T>(type);
 57	 return ctx.awakeable<T>(serde) as RestateAwakeable<T>;
 58	 },
 59	 run<T = void>(
 60	 name: string,
 61	 action: RestateRunAction<T>,
 62	 options: RunOptions<unknown> = {},
 63	 type?: ReceiveType<T>,
 64	 ): RestatePromise<T> {
 65	 if (type) {
 66	 const serde = createBSONSerde<T>(type);
 67	 return ctx.run(name, action, {
 68	 serde,
 69	 ...options,
 70	 }) as RestatePromise<T>;
 71	 }
 72	
 73	 return ctx.run(
 74	 name,
 75	 async () => {
 76	 await action();
 77	 },
 78	 options,
 79	 ) as RestatePromise<never>;
 80	 },
 81	 send(...args: readonly any[]): Promise<RestateInvocationHandle> {
 82	 const [key, { service, method, data }, options] =
 83	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 84	
 85	 const headers = config?.server?.forwardRequestHeaders
 86	 ? {
 87	 ...ctx.request().headers,
 88	 ...options?.headers,
 89	 }
 90	 : options?.headers;
 91	
 92	 const { invocationId } = ctx.genericSend({
 93	 service,
 94	 method,
 95	 parameter: data,
 96	 delay: options?.delay,
 97	 headers,
 98	 key,
 99	 });
 100	
 101	 return invocationId.then(invocationId => ({
 102	 invocationId,
 103	 }));
 104	 },
 105	 call<T>(...args: readonly any[]): RestatePromise<T> {
 106	 const [key, { service, method, data, deserializeReturn }, options] =
 107	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 108	
 109	 const headers = config?.server?.forwardRequestHeaders
 110	 ? {
 111	 ...ctx.request().headers,
 112	 ...options?.headers,
 113	 }
 114	 : options?.headers;
 115	
 116	 return ctx
 117	 .genericCall({
 118	 service,
 119	 method,
 120	 parameter: data,
 121	 headers,
 122	 key,
 123	 outputSerde: restate.serde.binary,
 124	 })
 125	 .map((value, failure) => {
 126	 if (value) {
 127	 return decodeRestateServiceMethodResponse(value, deserializeReturn);
 128	 }
 129	
 130	 if (
 131	 failure instanceof restate.TerminalError &&
 132	 failure.code === CUSTOM_TERMINAL_ERROR_CODE
 133	 ) {
 134	 deserializeBSONAndThrowCustomTerminalError(failure.message);
 135	 }
 136	
 137	 throw failure;
 138	 });
 139	 },
 140	 };
 141	}
